import React,{useEffect, useState} from 'react';
import {  Link } from "react-router-dom";


const Login = () => {
  //Main array of object State
  const [main,setMain] = useState([])
  //input values
  const[email,setEmail] = useState('')
  const [password,setPassword] = useState('')

 //for Submit Event
  const handleClick=(e)=>{
    e.preventDefalut();
    //Creating an Object
  let main={
    email,
    password
  }
  setMain([...main,main])
  setEmail('')
  setPassword('')

  //
  useEffect(()=>{
   localStorage.setItem('main',JSON.stringify(main));
  },[main])


  }


  return (
    <div className='main-box'>
    <div className='img-box'></div>
    <div className='login'>
    <h2>Login</h2>
    <form >
    <h4>Email or mobile number</h4>
    <input type="email" value={email} onChange={(e)=>setEmail(e.target.value)}/>
    <label><h4>Password</h4></label>     
    <h4><a href='#'>Forget Password</a></h4>
    <input type="password"  value={password}  onChange={(e)=>setPassword(e.target.value)}/><br/><br/>
    <button className='btn1' onClick={'handleClick'}>Login</button><br/>
    <br/>



    ------------------New to Amazone-------------------<br/><br/>




    <button className='btn1'><Link to="/sign">I am a new Customer</Link></button><br/>




    </form>
    </div>
    </div>



  )
}


export default Login
